mapper.select:
select ifnull(count(*),0)
  from member
 where userId = #{userId}

SELECT *
  FROM MEMBER
 WHERE userId=#{userId}
   AND userPw=#{userPw}
   AND userDeleteYn="N"

SELECT * FROM MEMBER
where userName like concat('%',#{searchkeyword},'%') where userId like concat('%',#{searchkeyword},'%') where userPosition like concat('%',#{searchkeyword},'%') where userRank like concat('%',#{searchkeyword},'%')
order by (case userDeleteYn when "N" then 1 else 2 end) limit #{currentpageDB},#{paging};

SELECT m.userId as loginUserId, m.userName as loginUserName, j.userId as joinUserId, j.projectNo as joinProjectNo, j.leaderYn as leaderYn FROM MEMBER m left outer join joinm j on (m.userId = j.userId and j.projectNo=#{projectNo}) where m.userDeleteYn="N" and m.adminYn ="N"
and (m.userName like concat('%',#{searchkeyword},'%') or m.userId like concat('%',#{searchkeyword},'%'))
order by (case leaderYn when "Y" then 1 when "N" then 2 else 3 end) limit #{currentpageDB},#{paging};

   SELECT *
	 FROM MEMBER 
	WHERE userId=#{userId}
	  AND userPw=#{userPw}

mapper.update:
 update member
	set	userDeleteYn="Y"
  where userId=#{gibonId};

 update member
	set	userDeleteYn="Y"
  where userId=#{chk};

mapper.select:
SELECT M.USERID AS userId, M.USERNAME AS userName, M.USERRANK AS userRank, M.USERPOSITION AS userPosition, group_concat(P.PROJECTNAME) AS projectName, (SELECT COUNT(*) FROM workchecklist W LEFT OUTER JOIN PROJECT P ON P.PROJECTNO=W.PROJECTNO WHERE M.USERID=W.USERID AND P.PROJECTDELETEYN="N" AND W.STATE="진행중" group by W.USERID) AS workCount FROM MEMBER M LEFT OUTER JOIN JOINM J ON M.USERID=J.USERID LEFT OUTER JOIN PROJECT P ON J.PROJECTNO=P.PROJECTNO and P.PROJECTDELETEYN="N" and p.projectState!="bg-success" WHERE M.USERDELETEYN="N" AND M.ADMINYN="N"
and userName like concat('%',#{searchkeyword},'%') and userPosition like concat('%',#{searchkeyword},'%') and userRank like concat('%',#{searchkeyword},'%')
GROUP BY M.USERID limit #{currentpageDB},#{paging}

select * from member
 where userId=#{userId}
   and userDeleteYn="N"

select count(*) 
  from member
 where userId=#{userId}
   and userPw=#{userPw}
