mapper.update:
	 update workchecklist
	    set state="진행중",
			updateDate=now(),
			updateUserId=#{userId},
			userId=#{JoinId}
  where projectNo=#{projectNo}
  	and workNo=#{workNo}

     update project
		set	projectDeleteYn="Y"
	  where projectNo=#{projectNo};

mapper.select:
select w.projectNo as projectNo, w.workNo as workNo, w.workNum as workNum, w.workSubject as workSubject, concat(substring(w.startDate,1,4),".",substring(w.startDate,5,2),".",substring(w.startDate,7,2)) as startDate, concat(substring(w.endDate,1,4),".",substring(w.endDate,5,2),".",substring(w.endDate,7,2)) as endDate, w.state as state, w.memo as memo, m.userName as userName, m.userId as userId from workchecklist w left outer join member m on w.userId=m.userId where w.projectNo=#{projectNo} and w.state="진행중"
and workNum like concat('%',#{searchkeyword1},'%') and workSubject like concat('%',#{searchkeyword1},'%')
order by (case m.userId when #{userId} then 1 else 2 end),(endDate-startDate) limit #{currentpageDB},#{paging}

mapper.delete:
delete 
  from workchecklist
 where projectNo=#{projectNo}
   and workNo=#{workNo}

mapper.select:
select *
  from workchecklist
 where projectNo=#{projectNo}
   and workNo=#{workNo}

mapper.update:
 update workchecklist
    set workNum=#{workNum},
		workSubject=#{workSubject},
		startDate=#{startDate},
		endDate=#{endDate},
		workContent=#{workContent},
		updateDate=now(),
		updateUserId=#{userId}
  where projectNo=#{projectNo}
  	and workNo=#{workNo}

	 update workchecklist
	    set state="진행중",
			updateDate=now(),
			updateUserId=#{userId},
			userId=#{userId}
  where projectNo=#{projectNo}
  	and workNo=#{workNo}

mapper.select:
select *
  from workchecklist
 where projectNo=#{projectNo}
   and workNo=#{workNo}

mapper.update:
 update workchecklist
	set state="진행완료",
		updateDate=now(),
		updateUserId=#{userId}
  where projectNo=#{projectNo}
  	and workNo=#{workNo}

mapper.delete:
delete 
  from workchecklist
 where projectNo=#{projectNo}
   and workNo=#{workNo}

mapper.select:
select count(*) 
  from workchecklist
 where userId=#{joinId}
   and projectNo=#{projectNo}
   and state="진행중"

select w.projectNo as projectNo, w.workNo as workNo, w.workNum as workNum, w.workSubject as workSubject, concat(substring(w.startDate,1,4),".",substring(w.startDate,5,2),".",substring(w.startDate,7,2)) as startDate, concat(substring(w.endDate,1,4),".",substring(w.endDate,5,2),".",substring(w.endDate,7,2)) as endDate, w.state as state, w.memo as memo, m.userName as userName, m.userId as userId from workchecklist w left outer join member m on w.userId=m.userId left outer join project p on p.projectNo=w.projectNo where m.userId=#{userId} and w.state="진행중" and p.projectDeleteYn="N"
and workNum like concat('%',#{searchkeyword1},'%') and workSubject like concat('%',#{searchkeyword1},'%')
limit #{currentpageDB},#{paging}
